/**
 * Send eth from escrow
 * @param  {string}  receiver ethereum address
 * @param  {number}  amount   max balance
 * @param  {object}  keystore deserialise keystore
 * @param  {string}  password
 * @return {Promise}
 */
export const dtrSendCoin = async (receiver, amount, keystore, password) => {
  const keys = await decodeKeystore(keystore, password);
  try {
    const dtrContractInstance = await getSignedContract(keys);
    return dtrContractInstance.sendCoin(
      add0x(receiver),
      parseInt(UTILITYWEB3.toWei(amount, 'ether'), 10),
      {
        from: keys.address,
        gas: 200000,
        gasPrice: 25000000000,
      },
    );
  } catch (e) {
    return new TypeError(e);
  }
};

import fetch from 'isomorphic-fetch';
import DetherJson from 'dethercontract/contracts/DetherInterface.json';
import DetherStorageJson from 'dethercontract/contracts/DetherStorage.json';

import { UTILITYWEB3 } from './constants/appConstants';

const etherScanKey = process.env.DETHER_ETHERSCAN_KEY;

/**
 * Get all tsx
 * @param  {string} address ethereum address
 * @param  {number} network network ethereum
 * @return {array} all tsx
 */
const getAllTsx = (address, network) =>
  new Promise((resolve, reject) => {
    const dtr = DetherJson.networks[network.networkNumber].address;
    const storage = DetherStorageJson.networks[network.networkNumber].address;

    fetch(`https://kovan.etherscan.io/api?module=account&action=txlist&address=
      ${address}&startblock=0&endblock=99999999&sort=asc&apikey=${etherScanKey}`)
      .then(res => res.json()).then((transac) => {
        const formatedTsx = transac.result.map((tsx) => ({
          etherscan: `https://kovan.etherscan.io/tx/${tsx.hash}`,
          value: UTILITYWEB3.fromWei(tsx.value, 'ether'),
          to: tsx.to,
          from: tsx.from,
          date: new Date(parseInt(tsx.timeStamp * 1000, 10)).toLocaleString('en-US', { hour12: false }),
          timeStamp: tsx.timeStamp,
          error: tsx.isError === '1',
          receive: tsx.from === address,
          myEther: tsx.contractAddress === dtr || tsx.contractAddress === storage || tsx.to === dtr,
        }));
        fetch(`https://kovan.etherscan.io/api?module=account&action=txlistinternal&address=
          ${address}&startblock=0&endblock=99999999&sort=asc&apikey=${etherScanKey}`)
          .then(res => res.json()).then((iTransac) => {
            const formatedTsx2 = iTransac.result.map((tsx2) => ({
              etherscan: `https://kovan.etherscan.io/tx/${tsx2.hash}`,
              value: UTILITYWEB3.fromWei(tsx2.value, 'ether'),
              to: tsx2.to,
              from: tsx2.from,
              date: new Date(parseInt(tsx2.timeStamp * 1000, 10)).toLocaleString('en-US', { hour12: false }),
              timeStamp: tsx2.timeStamp,
              error: tsx2.isError === '1',
              receive: tsx2.from === address,
              myEther: tsx2.contractAddress === dtr ||
                tsx2.contractAddress === storage || tsx2.to === dtr,
            }));

            const allTsx = formatedTsx2.concat(formatedTsx);
            allTsx.sort((a, b) => b.timeStamp - a.timeStamp);
            resolve(allTsx);
          });
      }).catch((msg) => {
        reject(msg);
      });
  });

export default getAllTsx;

import { UTILITYWEB3 } from './constants/appConstants';

/**
 * Get eth Balance
 * @param  {string} address ethereum address
 * @return {number} return balance
 */
const getEthBalance = address =>
  new Promise((resolve, reject) => {
    UTILITYWEB3.eth.getBalance(address, (err, res) => {
      if (!err) {
        resolve(UTILITYWEB3.fromWei(res, 'ether').toNumber());
      } else {
        reject(new TypeError({ message: err, arg: address }));
      }
    });
  });

export default getEthBalance;

import { getContractStorageInstance, getContractInstance, UTILITYWEB3 } from './constants/appConstants';

/**
 * get teller by address
 * @param  {string}  address ethereum address
 * @return {object} return teller
 */
export const dtrGetTeller = async (address) => {
  const teller = {};
  const dtrContractInstance = await getContractInstance();
  const tellerPos = await dtrContractInstance.getTellerPos(address);
  if (tellerPos[3].toNumber() === 0) return null;
  const tellerProfile = await dtrContractInstance.getTellerProfile(address);
  try {
    teller.name = UTILITYWEB3.toUtf8(tellerProfile[3]);
  } catch (e) {
    teller.name = 'Dether';
  }
  try {
    teller.messengerAddr = UTILITYWEB3.toUtf8(tellerProfile[6]);
  } catch (e) {
    teller.messengerAddr = 'Dether';
  }
  return {
    messengerAddr: teller.name,
    name: teller.messengerAddr,
    lat: tellerPos[0].toNumber(),
    lng: tellerPos[1].toNumber(),
    zoneId: tellerPos[2].toNumber(),
    escrowBalance: UTILITYWEB3.fromWei(tellerPos[3].toNumber(), 'ether'),
    rates: tellerProfile[0].toNumber(),
    volumeTrade: UTILITYWEB3.fromWei(tellerProfile[1].toNumber(), 'ether'),
    nbTrade: tellerProfile[2].toNumber(),
    currencyId: tellerProfile[4].toNumber(),
    avatarId: tellerProfile[5].toNumber(),
    ethAddress: address,
    id: address,
  };
};

/**
 * Get All tellers per zone
 * @param  {string}  zone
 * @return {array} return array of tellers
 */
export const getTellersPerZone = async (zone) => {
  const dtrContractStorageInstance = await getContractStorageInstance();
  const tellersAddressesInZone = await dtrContractStorageInstance.getZone.call(zone);
  const tellers = [];
  await Promise.all(tellersAddressesInZone.map(async (data) => {
    const teller = await dtrGetTeller(data);
    if (teller && teller.zoneId === parseInt(zone, 10) && tellers.indexOf(teller)) {
      tellers.push(teller);
    }
  }));
  return tellers;
};

/**
 * Get All tellers on the map
 * @return {array} return array of tellers
 */
export const getAllTellers = async () => {
  const dtrContractStorageInstance = await getContractStorageInstance();
  const tellersAddressesInZone = await dtrContractStorageInstance.getAllTellers();
  const tellers = [];
  await Promise.all(tellersAddressesInZone.map(async (data) => {
    const teller = await dtrGetTeller(data);
    if (teller && tellers.indexOf(teller)) tellers.push(teller);
  }));
  return tellers;
};

import decodeKeystore from './utils/decodeKeystore';
import getSignedWeb3 from './utils/contractInstance';
import { GAS_PRICE, UTILITYWEB3 } from './constants/appConstants';
import add0x from './utils/add0x';

/**
 * Send eth
 * @param  {string} address eth address
 * @param  {number} amount max balance
 * @param  {object} keystore deserialize keystore
 * @param  {string} password
 * @return {object} Return txs
 */
const sendEther = (address, amount, keystore, password) => (
  new Promise(async (resolve, reject) => {
    try {
      const keys = await decodeKeystore(keystore, password);
      const signedWeb3 = getSignedWeb3(keys);
      const balance = await UTILITYWEB3.eth.getBalance(keys.address);

      let tsxAmount = parseInt(UTILITYWEB3.toWei(amount, 'ether'), 10);

      if (balance.toNumber() < (tsxAmount + (GAS_PRICE * 21000))) {
        tsxAmount = balance.toNumber() - (GAS_PRICE * 21000);
      }

      signedWeb3.eth.sendTransaction({
        from: add0x(keys.address),
        to: add0x(address),
        value: parseInt(tsxAmount, 10),
        gas: 21000,
        GAS_PRICE,
      }, (error, hash) => {
        if (!error) {
          const retvalue = {
            from: add0x(keys.address),
            to: add0x(address),
            value: amount,
            date: new Date().toLocaleString('en-US', { hour12: false }),
            receive: 'no',
            myEther: 'no',
            etherscan: `https://kovan.etherscan.io/tx/${hash}`,
          };
          resolve(retvalue);
        } else {
          console.log(error);
          reject(error);
        }
      });
    } catch (error) {
      reject(error);
    }
  })
);

export default sendEther;

/**
 * @param {string} input address ethereum
 * @return {string} formated address ethereum
 */
const add0x = (input) => {
  if (typeof (input) !== 'string') return input;
  else if (input.length < 2 || input.slice(0, 2) !== '0x') return `0x${input}`;
  return input;
};

export default add0x;

import Web3 from 'web3';
import contract from 'truffle-contract';
import DetherJson from 'dethercontract/contracts/DetherInterface.json';
import SignerProvider from 'ethjs-provider-signer';
import { sign } from 'ethjs-signer';

import add0x from './add0x';

const providerUrl = process.env.DETHER_ETHEREUM_PROVIDER;

/**
 * @param {string} privateKey ethereum private key
 * @param {string} address ethereum address
 * @return {object} return dtrContract instance
 */
export const getSignedContract = (privateKey, address) => {
  if (!privateKey || !address) return null;
  const dtrContract = contract(DetherJson);
  const provider = new SignerProvider(providerUrl, {
    signTransaction: (rawTx, cb) => cb(null, sign(rawTx, add0x(privateKey))),
    accounts: cb => cb(null, address),
  });
  dtrContract.setProvider(provider);
  return dtrContract.deployed();
};

/**
 * get signed web3
 * @param
 * @param.privateKey  {string} privateKey
 * @param.address  {string} address
 * @return {object}
 */
export const getSignedWeb3 = ({ privateKey, address }) => {
  if (!privateKey || !address) return null;
  const provider = new SignerProvider(providerUrl, {
    signTransaction: (rawTx, cb) => cb(null, sign(rawTx, add0x(privateKey))),
    accounts: cb => cb(null, address),
  });
  return new Web3(provider);
};

/**
 * @param {object} keystore
 * @param {string} password
 * @return {object} return privateKey and ethereum address
 */
const decodeKeystore = (keystore, password) =>
  new Promise((resolve, reject) => {
    if (!keystore || !password) return reject(new TypeError('Invalid arguments'));

    return keystore.keyFromPassword(password, (err, pwDerivedKey) => {
      if (err) return reject(err);

      if (!keystore.isDerivedKeyCorrect(pwDerivedKey)) {
        return reject(new TypeError('Invalid password'));
      }

      const address = keystore.getAddresses()[0];
      const privateKey = keystore.exportPrivateKey(address, pwDerivedKey);

      return resolve({
        address,
        privateKey,
      });
    });
  });

export default decodeKeystore;
